//Component for Admin Inventory

import React from 'react';
import axios from "axios";
import InventoryItems from './inventoryItems';
import InventoryCartItem from './inventoryCart';

class Inventory extends React.Component {
    constructor() {
        super();
        this.state = {inventoryData: [],inventoryCart:[]};

        this._addToCartHandler = this._addToCartHandler.bind(this);
    }

    componentDidMount() {
      this._getInventoryData();
    }

    _getInventoryData() {
      let url = 'https://dev-african-roots.herokuapp.com/api/inventory';
      return axios(url, {
          method: 'GET',
          mode: 'no-cors',
          headers: {
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'application/json',
          },
          withCredentials: false,
          credentials: 'same-origin',
        }).then(response => {
          this.setState({
            inventoryData: response.data
          });
          console.log(this.state.inventoryData);
        })
    }

    _addToCartHandler(name,quantity,unit){
        console.log(name);
        console.log(quantity);

        let inventoryCart = this.state.inventoryCart;

        if (inventoryCart.length < 1) {
            inventoryCart.push({name:name,quantity:quantity,unit:unit});
        } else {
            for (var i=0; i < inventoryCart.length; i++) {
                if (inventoryCart[i].name == name) {
                    inventoryCart[i].quantity  = inventoryCart[i].quantity + 1;
                } else {
                    inventoryCart.push({name:name,quantity:quantity,unit:unit});

                }
            }
        }

        this.setState({
            inventoryCart: inventoryCart
          });
    }

    render() {
      return (
        <div className="tab-pane" role="tabpanel" id="tab-2">
        <div className="row">
            <div className="col-12 col-md-6 col-lg-7 col-xl-8">
                <div className="row InventoryFilterBG">
                    <div className="col-4 col-md-2 filterItem" id="filterFood"><img className="d-table filterIcon" src="assets/img/groceries.svg"></img>
                        <p className="d-table DarkGreen filterItemCaption">All</p>
                    </div>
                    <div className="col-4 col-md-2 filterItem" id="filterDesserts"><img className="d-table filterIcon" src="assets/img/fruits.svg"></img>
                        <p className="d-table filterItemCaption">Fruits</p>
                    </div>
                    <div className="col-4 col-md-2 filterItem" id="filterDrinks"><img className="d-table filterIcon" src="assets/img/vegetables.svg"></img>
                        <p className="d-table filterItemCaption">Vegetables</p>
                    </div>
                    <div className="col-4 col-md-2 filterItem" id="filterDrinks"><img className="d-table filterIcon" src="assets/img/garlic.svg"></img>
                        <p className="d-table filterItemCaption">Flour | Spices</p>
                    </div>
                    <div className="col-4 col-md-2 filterItem" id="filterDrinks"><img className="d-table filterIcon" src="assets/img/almond.svg"></img>
                        <p className="d-table filterItemCaption">Seeds | Nuts</p>
                    </div>
                    <div className="col-4 col-md-2 filterItem" id="filterDrinks"><img className="d-table filterIcon" src="assets/img/almond-milk.svg"></img>
                        <p className="d-table filterItemCaption">Liquids</p>
                    </div>
                </div>
            </div>
            <div className="col-12 col-md-6 col-lg-5 col-xl-4 ml-xl-auto">
                <div className="row InventoryFilterBG">
                    <div className="col-8">
                        <div><a className="btn btn-light collapseItemCart" data-toggle="collapse" aria-expanded="false" aria-controls="collapse-1" role="button" href="#collapse-1">Ingredients Cart&nbsp;<i className="material-icons downIcon">keyboard_arrow_down</i></a>
                            <div className="collapse ingredientOrderCart" id="collapse-1">
                                <div className="row ingredientCartItem">
                                    <div className="col-4 ml-auto">
                                        <p className="text-center text-secondary FoodNameLabelBlack">AMOUNT</p>
                                    </div>
                                </div>
                                {this.__showInventoryCartItems()}
                                <div className="row ingredientOrderBtn">
                                    <div className="col"><button className="btn btn-dark btn-lg btnLogin" type="button">Order Items</button></div>
                                </div>
                        </div>
                    </div>
                </div>
                <div className="col ingredientCartAmount">
                    <h6 className="text-right">{this.state.inventoryCart.length} ITEMS</h6>
                </div>
            </div>
        </div>
        </div>
        <div className="row inventoryItemsContainer">
            {this.__showInventoryItems()}
            <div className="col"></div>
        </div>
        </div>

        );
    }

    __showInventoryItems() {

        if (this.state.inventoryData.length > 0) {
          return this.state.inventoryData.map(item => {
            return (
            <InventoryItems
                key={item._id}
                name={item.name}
                unit={item.unit}
                quantity={item.quantity}
                image={item.image}
                addToCart={this._addToCartHandler}
            />
            );
          });
        } else {
          return null;
        }
      }

      __showInventoryCartItems() {

        if (this.state.inventoryCart.length > 0) {
          return this.state.inventoryCart.map(item => {
            return (
            <InventoryCartItem
                key={item.name}
                name={item.name}
                unit={item.unit}
                quantity={item.quantity}
                addToCart={this._addToCartHandler}
                removeFromCart={this._addToCartHandler}
            />
            );
          });
        } else {
          return null;
        }
      }
}

export default Inventory
