import React from 'react'
import {Doughnut} from 'react-chartjs-2';

class OrdersChart extends React.Component {
    constructor(props) {
        super(props);
        this.state =  {data:{
            labels: ["Beakfasts", "Light Meals", "Drinks", "Desserts"],
            datasets: [{
            backgroundColor: ['#D6D6D6','#F5F5F5','#D1E902','#B4CF23'],
            borderColor: ['#D6D6D6','#F5F5F5','#D1E902','#B4CF23'],
            data: [9, 10, 5, 2],
            }]
        }, options:{
            legend: {
            display: false
            },
            layout: {
                padding: {
                    top: 15,
                    bottom: 0
                }
            }
        }};
      }

    render() {
        return (
            <div className="chartContainer">
                <div className="col">
                    <h5 className="chartHeadingSmall">ORDERS</h5>
                    <h5 className="chartHeading">Frequently Ordered</h5>
                </div>
                <div className="col">
                < Doughnut data = {this.state.data}
                            options = {this.state.options}
                            height={250}/>
                <div className="row" style={{marginBottom: '-10px', paddingLeft:'20px', paddingRight:'20px'}}>
                    <div className="col-1" style={{padding: '0'}}>
                        <div style={{backgroundColor: '#be3f3f',width: '15px',height: '15px',borderRadius: '10px'}}></div>
                    </div>
                    <div className="col-auto">
                        <h6 className="text-right" style={{margin: '0'}}>Breakfasts</h6>
                    </div>
                    <div className="col-auto ml-auto">
                        <p className="text-right cartAmountTxt" style={{marginTop: '0'}}>R 100</p>
                    </div>
                </div>
                </div>
            </div>
    );
    }
}    

export default OrdersChart
