import React from 'react';
import axios from "axios";
import Header from './Header';
import menuItem from './MenuItem';

class Menu extends React.Component {
    constructor() {
        super();
        this.state = {inventoryData: [],inventoryCart:[],filter:"all"};
        this._orderItems = this._orderItems.bind(this);
        this._addToCartHandler = this._addToCartHandler.bind(this);
        this._removeFromCartHandler = this._removeFromCartHandler.bind(this);
    }

    componentDidMount() {
      this._getInventoryData();
    }

    _getInventoryData() {
      let url = 'https://dev-african-roots.herokuapp.com/api/inventory';
      return axios(url, {
          method: 'GET',
          mode: 'no-cors',
          headers: {
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'application/json',
          },
          withCredentials: false,
          credentials: 'same-origin',
        }).then(response => {
          this.setState({
            inventoryData: response.data
          });
          console.log(this.state.inventoryData);
        })
    }

    _orderItems(){

        //add update function here

        this.setState({
            inventoryCart: []
          });
    }

    _addToCartHandler(name,quantity,unit){

        let inventoryCart = this.state.inventoryCart;

        if (inventoryCart.length < 1) {
            inventoryCart.push({name:name,quantity:quantity,unit:unit});
        } else {
            inventoryCart = this._searchInventory(inventoryCart,name,quantity,unit);
        }

        this.setState({
            inventoryCart: inventoryCart
          });
    }

    _removeFromCartHandler(name,quantity,unit) {

        let inventoryCart = this.state.inventoryCart;

        for (var i=0; i < inventoryCart.length; i++) {
            if (inventoryCart[i].name == name) {
                inventoryCart[i].quantity  = inventoryCart[i].quantity - 10;
                if (inventoryCart[i].quantity < 1) {
                    inventoryCart.splice(i,1);
                }
            }
        }


        this.setState({
            inventoryCart: inventoryCart
          });
    }

    _searchInventory(inventoryCart,name,quantity,unit){

        let isSame = false;
        for (var i=0; i < inventoryCart.length; i++) {
            if (inventoryCart[i].name == name) {
                inventoryCart[i].quantity  = inventoryCart[i].quantity + 10;
                return inventoryCart;
            } else {
                isSame = true;
            }
        }
        if (isSame = true){
          inventoryCart.push({name:name,quantity:quantity,unit:unit});
          return inventoryCart;
        }
    }

    // _passFilter(items){

    //     let isSame = false;
    //     for (var i=0; i < inventoryCart.length; i++) {
    //         if (inventoryCart[i].name == name) {
    //             inventoryCart[i].quantity  = inventoryCart[i].quantity + 10;
    //             return inventoryCart;
    //         } else {
    //             isSame = true;
    //         }
    //     }
    //     if (isSame = true){
    //       inventoryCart.push({name:name,quantity:quantity,unit:unit});
    //       return inventoryCart;
    //     }
    // }

    render() {
      return (
        <div>
                <div className="background" style={{backgroundSize: '200px',backgroundImage: `url(assets/img/Asset%203.svg)`}}></div>
                {/*Nav*/}
                <Header />
                {/*/Nav*/}


                {/*Body Container*/}
                <div className="container">
                    <div className="row">

                    {/*Menu Cards Container*/}
                    <div className="col-12 col-lg-8 cardsContainer">
                    {/* <div className="row" style={{margin: '0px'}}> */}

                    {/*Menu Card*/}
                    {/* <MenuItem /> */}
                    <menuItem />
                    {/*/Menu Card*/}
                    {/* </div> */}
                    </div>
                    {/*/Menu Cards Container*/}

                    {/* Cart Container */}
                    {/* <Cart /> */}
                    <Cart />
                    {/* /Cart Container */}

                    {/* Receipts Container */}
                    <Reciepts />
                    {/* /Receipts Container */}
                    </div>
                </div>

                {/* /Body Container */}

                {/*Menu Item Details Modal*/}
                <menuItemDetails />
                {/*/Menu Item Details Modal*/}

                {/*Log In Modal*/}
                <Login />
                {/*/Log In Modal*/}

                {/*Sign Up Modal*/}
                <CreateAccount />
                {/*/Sign Up Modal*/}

            </div>

        );
    }

    __showInventoryItems() {
        // filteredData = _passFilter(this.state.inventoryData);
        if (this.state.inventoryData.length > 0) {
          return this.state.inventoryData.map(item => {
            return (
            <InventoryItems
                key={item._id}
                name={item.name}
                unit={item.unit}
                quantity={item.quantity}
                image={item.image}
                addToCart={this._addToCartHandler}
            />
            );
          });
        } else {
          return null;
        }
      }

      __showInventoryCartItems() {

        if (this.state.inventoryCart.length > 0) {
          return this.state.inventoryCart.map(item => {
            return (
            <InventoryCartItem
                key={item.name}
                name={item.name}
                unit={item.unit}
                quantity={item.quantity}
                addToCart={this._addToCartHandler}
                removeFromCart={this._removeFromCartHandler}
            />
            );
          });
        } else {
          return null;
        }
      }
}

export default Menu
