import React from 'react'
import {Doughnut} from 'react-chartjs-2';
import OrdersChartKey from './ordersKey';
import axios from "axios";

class OrdersChart extends React.Component {
    constructor(props) {
        super(props);
        this.state =  {
            orderData: [],
            mealData: [],
            data:{
            labels: ["Beakfasts", "Light Meals", "Drinks", "Desserts"],
            datasets: [{
            backgroundColor: ['#D6D6D6','#F5F5F5','#D1E902','#B4CF23'],
            borderColor: ['#D6D6D6','#F5F5F5','#D1E902','#B4CF23'],
            data: [9, 10, 5, 2],
            }]
        }, options:{
            legend: {
            display: false
            },
            layout: {
                padding: {
                    top: 15,
                    bottom: 20
                }
            }
        }};
    }

    componentDidMount() {
    this._getOrderData();
    this._getMealData();
    this._generateData();
    }

    _getOrderData() {
    let url = 'https://dev-african-roots.herokuapp.com/api/orders';
    return axios(url, {
        method: 'GET',
        mode: 'no-cors',
        headers: {
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'application/json',
        },
        withCredentials: false,
        credentials: 'same-origin',
        }).then(response => {
            this.setState({
                orderData: response.data
              });
        })
    }

    _getMealData() {
        let url = 'https://dev-african-roots.herokuapp.com/api/meals';
        return axios(url, {
            method: 'GET',
            mode: 'no-cors',
            headers: {
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            },
            withCredentials: false,
            credentials: 'same-origin',
          }).then(response => {
            this.setState({
                mealData: response.data
              });
          })
      }    

    _searchMeals(mealID, mealData){
        let meals= [];
        for (var i=0; i < mealData.length; i++) {
            if (Array.isArray(mealID)) {
                for (var j=0; j < mealID.length; j++) {
                    if (mealData[i]._id === mealID[j]) {
                        meals.push(mealData[i])
                    }
                }
            }
            else if (mealData[i]._id === mealID) {
                meals.push(mealData[i])
            } 
        }

        return meals;
      }

    render() {
        return (
            <div className="chartContainer">
                <div className="col">
                    <h5 className="chartHeadingSmall">ORDERS</h5>
                    <h5 className="chartHeading">Frequently Ordered</h5>
                </div>
                <div className="col">
                < Doughnut data = {this.state.data}
                            options = {this.state.options}
                            height={250}/>
                < OrdersChartKey />
                </div>
            </div>
    );
    }
}    

export default OrdersChart
