//Component for Admin Dashboard

import React from 'react'
import ProfitsChart from './profits';
import OrdersChart from './orders';
import CustomersChart from './customers';
import SalesChart from './sales';
import axios from "axios";

class Dashboard extends React.Component {
    constructor(props) {
        super(props);
        this.state =  {
            orderData: [],
            mealData: []
        };
    }

componentDidMount() {
    this._getOrderData();
    this._getMealData();
    }

    _getOrderData() {
    let url = 'https://dev-african-roots.herokuapp.com/api/orders';
    return axios(url, {
        method: 'GET',
        mode: 'no-cors',
        headers: {
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'application/json',
        },
        withCredentials: false,
        credentials: 'same-origin',
        }).then(response => {
            this.setState({
                orderData: response.data
              });
        })
    }

    _getMealData() {
        let url = 'https://dev-african-roots.herokuapp.com/api/meals';
        return axios(url, {
            method: 'GET',
            mode: 'no-cors',
            headers: {
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            },
            withCredentials: false,
            credentials: 'same-origin',
          }).then(response => {
            this.setState({
