    };

    render() {
        return (
        <div className="tab-pane active" role="tabpanel" id="tab-1">
            <div className="row">
                <div className="col-12 col-md-7 col-lg-9">
                    <div className="row">
                        <div className="col-12 col-lg-7">
                            <ProfitsChart />
                        </div>
                        <div className="col-12 col-lg-5">
                            {this.__showOrdersChart()}
                        </div>
                        <div className="col-12 col-lg-5">
                            <CustomersChart />
                        </div>
                        <div className="col-12 col-lg-7">
                            <SalesChart />
                        </div>
                    </div>
                </div>
                <div className="col">
                    <div className="chartContainer inSeason">
                        <div className="col">
                            <h5 className="chartHeadingSmall">INGREDIENTS</h5>
                            <h5 className="chartHeading">What's in season</h5>
                        </div>
                        <div className="col inSeasonCol">
                            <div className="inSeasonCard">
                                <div className="inSeasonIngredientImageItem" style={{backgroundImage: 'url(assets/img/fa99fa2adb081f981d01e52b3f248bab.jpg)'}}></div>
                                <p className="text-center employeeItemName">Ingredient Name</p>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        );
    }

    __showOrdersChart() {
        let orderChartData = this._generateOrderChartData(this.state.orderData,this.state.mealData);
        if (this.state.orderData.length > 0) {
          return this.state.orderData.map(order => {
            if(order.order_status == 0){
              return (
                <OrdersChart
                  key={order._id}
                  orderNumber={order.order_number}
                  user={order.user_name}
                  meals={order.meal_id}
                />
              );
            };
          });
        } else {
          return null;
        }
