//List Item for Paid Column In Order List

import React from 'react'
import axios from "axios";

class UserListItem extends React.Component {
    constructor() {
      super();
      this.state = {orderData: [],mealData: []};
      this._handleClick = this._handleClick.bind(this);
    }

    componentDidMount() {
        this._getMealData();
      }

    _getMealData() {
        let url = 'https://dev-african-roots.herokuapp.com/api/meals';
        return axios(url, {
            method: 'GET',
            mode: 'no-cors',
            headers: {
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            },
            withCredentials: false,
            credentials: 'same-origin',
          }).then(response => {
            this.setState({
              mealData: response.data
            });
          })
      }

      _handleClick(){
        let data = {order_status:2}
        this._putOrder(data);
        console.log(data);
      };

      _putOrder(data) {
        axios({
            method: 'put',
            url: 'https://dev-african-roots.herokuapp.com/api/orders/'+this.props.order_id,
            data: data,
            config: { headers: {'Content-Type': 'multipart/form-data' }}
            })
            .then(response => {
                console.log("Yay!")
                console.log(response);
                //handle success
               this.props.getOrderData();
            })
            .catch(function (response) {
                //handle error
                console.log("awww!")
                console.log(response);
            });
      }

    _searchMeals(mealID, mealData){
        let meals= [];
        for (var i=0; i < mealData.length; i++) {
            if (Array.isArray(mealID)) {
                for (var j=0; j < mealID.length; j++) {
                    if (mealData[i]._id === mealID[j]) {
                        meals.push(mealData[i])
                    }
                }
            }
            else if (mealData[i]._id === mealID) {
                meals.push(mealData[i])
            } 
        }

        return meals;
      }


    render() {
      return (
        <div className="row OrderListRow">
        <div className="col receiptsCard" style={{paddingBottom: '15px'}}>
            <div className="row">
                <div className="col-12 d-inline-flex">
                    <p>{this.props.name}</p>
                </div>
                <div className="col-12 d-inline-flex">
                    <p className="d-xl-flex mr-auto FoodPriceLabelReceipt">{this.props.email}</p>
                </div>
                <div className="col">
                    <div className="cartItemDevider"></div>
                </div>
                <div className="col-12 d-inline-flex">
                    <div style={{width: '100%'}}><a className="btn btn-outline-link btn-sm d-lg-flex justify-content-lg-end" data-toggle="collapse" aria-expanded="false" aria-controls="{this.props.email}" role="button" href={'#'+this.props.email}>promote toÂ <i className="la la-angle-down float-right d-lg-flex greenIcon" style={{marginTop: '0px'}}></i></a>
                        <div className="collapse" id={this.props.name}>
                            <div className="row">
                                <div className="col"><button className="btn btn-light btn-lg btnUserChange" type="button" onClick={this._handleClick}>Customer</button></div>
                                <div className="col"><button className="btn btn-dark btn-lg btnServeOrder" type="button" onClick={this._handleClick}>Admin</button></div>
                            </div>
                    </div>
                </div>
            </div>
        </div>
    </div>
    </div>

        );
    }
}

export default UserListItem
