//Admin Nav

import React from 'react'

class AdminNav extends React.PureComponent {
  constructor(props) {
      super(props);
      this._handleLogout = this._handleLogout.bind(this);
    }

  _handleLogout() {
    sessionStorage.clear();
    this.props.logOut();
  }

  render() {
    const role = sessionStorage.getItem('userRole');

    if (role == 1 || role == 2) {
        view5 = 
        <li className="nav-item navHamburgerItem" role="presentation"><Link to={`/billing`} className="nav-link hamburgerNavLink">Billing</Link></li>
    }

    if (role == 2) {
        view6 = 
        <li className="nav-item navHamburgerItem" role="presentation"><Link to={`/dashboard`} className="nav-link hamburgerNavLink">Dashboard</Link></li>
    }
    return (  
      <div>
      <nav className="navbar navbar-light navbar-expand-md fixed-top navigation-clean">
        <div className="container"><a className="navbar-brand" href="#"><img className="d-none d-md-block navLogo" src="assets/img/Frame.svg" width="200px"></img><h4 className="text-uppercase d-md-none">login</h4></a><button className="navbar-toggler" data-toggle="collapse" data-target="#navcol-1"><span className="sr-only">Toggle navigation</span><span className="navbar-toggler-icon"></span></button>
            <div className="collapse navbar-collapse navCollapseCol" id="navcol-1">
                <ul className="nav navbar-nav text-uppercase ml-auto">
                    {view5}
                    {view6}
                    <li className="nav-item navHamburgerItem" role="presentation"><a className="nav-link hamburgerNavLink"><h5 className="DarkGreen" style={{marginTop:'14px'}}>{sessionStorage.getItem('userName')}</h5></a></li>
                    <li className="nav-item" role="presentation"><a className="nav-link hamburgerNavLink" href="#" data-toggle="modal" data-target="#signUpModal" onClick={this._handleLogout}>log out</a></li>
                </ul>
          </div>
        </div>
      </nav>
      <div className="background backgroundTabs" style={{backgroundSize: '200px', backgroundImage: 'url(assets/img/Asset%203.svg)'}}></div>
      </div>
        );
    }
}    

export default AdminNav
