import React from 'react'
import {Doughnut} from 'react-chartjs-2';
import OrdersChartKey from './ordersKey';
import axios from "axios";

class OrdersChart extends React.Component {
    constructor(props) {
        super(props);
        this.state =  {
            data:{
            labels: this.props.labels,
            datasets: [{
            backgroundColor: this.props.colours,
            borderColor: this.props.colours,
            data: this.props.data,
            }]
        }, options:{
            legend: {
            display: false
            },
            layout: {
                padding: {
                    top: 15,
                    bottom: 20
                }
            }
        }};
    }

    render() {
        return (
            <div className="chartContainer">
                <div className="col">
                    <h5 className="chartHeadingSmall">ORDERS</h5>
                    <h5 className="chartHeading">Frequently Ordered</h5>
                </div>
                <div className="col">
                < Doughnut data = {this.state.data}
                            options = {this.state.options}
                            height={250}/>
                < OrdersChartKey />
                </div>
            </div>
    );
    }

    __showMenuItem() {
        let meals = this._searchMeals(this.props.meals,this.state.mealData);
        if (meals.length > 0) {
        return meals.map(meal => {
            return (
                <RecieptMenuItem
                key={meal._id}
                name={meal.name}
                price={meal.price_id}
                />
            );
        });
        } else {
        return null;
        }
    }
}    

export default OrdersChart
