//List Item for Paid Column In Order List

import React from 'react'
import axios from "axios";
import RecieptMenuItem from '../../ClientUI/Menu/RecieptMenuItem';

class OrderListItemBtn extends React.Component {
    constructor() {
      super();
      this.state = {orderData: [],mealData: []};
    }

    componentDidMount() {
        this._getMealData();
      }

    _getMealData() {
        let url = 'https://dev-african-roots.herokuapp.com/api/meals';
        return axios(url, {
            method: 'GET',
            mode: 'no-cors',
            headers: {
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            },
            withCredentials: false,
            credentials: 'same-origin',
          }).then(response => {
            this.setState({
              mealData: response.data
            });
          })
      }

      _putOrder() {
        console.log(this.state.menuCart.length);
        if (this.state.menuCart.length > 0){
            console.log("la")
        let data = {user_name:sessionStorage.getItem('userName'), 
                    user_id:sessionStorage.getItem('userId'), 
                    order_number:this._generateOrderNumber(),
                    order_status:0,
                    meal_id:this._getMenuIds()}
        console.log(data);
        axios({
            method: 'post',
            url: 'https://dev-african-roots.herokuapp.com/api/order'+this.props,
            data: data,
            config: { headers: {'Content-Type': 'multipart/form-data' }}
            })
            .then(response => {
                //handle success
                this.setState({
                    menuCart: [],
                    badge:'1'
                });
                this._updateCartTotal();
                this.refs.receipts._getReceiptOrderData();
            })
            .catch(function (response) {
                //handle error
                console.log("awww!")
                console.log(response);
            });
        }
      }

    _searchMeals(mealID, mealData){
        let meals= [];
        for (var i=0; i < mealData.length; i++) {
            if (Array.isArray(mealID)) {
                for (var j=0; j < mealID.length; j++) {
                    if (mealData[i]._id === mealID[j]) {
                        meals.push(mealData[i])
                    }
                }
            }
            else if (mealData[i]._id === mealID) {
                meals.push(mealData[i])
            } 
        }

        return meals;
      }


    render() {
      return (
        <div className="row OrderListRow">
            <div className="col receiptsCard">
                <div className="row">
                    <div className="col-6 d-inline-flex">
                        <p>{this.props.user}</p>
                    </div>
                    <div className="col-6 d-inline-flex">
                        <p className="d-xl-flex ml-auto receiptTimestamp">Order No.</p>
                        <p className="d-xl-flex ml-auto FoodPriceLabelReceipt">{this.props.orderNumber}</p>
                    </div>
                    <div className="col">
                        <div className="cartItemDevider"></div>
                    </div>
                    {/* Order List Menu Item */}
                    {this.__showMenuItem()}
                    {/* /Order List Menu Item */}
                </div>

                <div className="row">
                    <div className="col-6 col-lg-8 col-xl-6 ml-auto"><button className="btn btn-dark btn-lg btnServeOrder" type="button">Serve Order</button></div>
                </div>
            </div>
        </div>

        );
    }

    __showMenuItem() {
        let meals = this._searchMeals(this.props.meals,this.state.mealData);
        if (meals.length > 0) {
        return meals.map(meal => {
            return (
                <RecieptMenuItem
                key={meal._id}
                name={meal.name}
                price={meal.price_id}
                />
            );
        });
        } else {
        return null;
        }
    }
}

export default OrderListItemBtn
