//Tab For Viewing a List of Orders

import React from 'react'
import axios from "axios";
import UserListItem from './UserListItem';

class UsersTab extends React.Component {
  constructor() {
      super();
      this.state = {userData: []};
      this._getUserData = this._getUserData.bind(this);
  }

  componentDidMount() {
    this._getUserData();
  }

  _getUserData() {
    let url = 'https://dev-african-roots.herokuapp.com/api/admin/users';
    return axios(url, {
        method: 'GET',
        mode: 'no-cors',
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
        },
        withCredentials: false,
        credentials: 'same-origin',
      }).then(response => {
        this.setState({
          userData: response.data
        });
        console.log(this.state.userData);
      })
  }


  render() {
    return (
    <div className="tab-pane" role="tabpanel" id="tab-3">
      <div className="row">
          <div className="col-lg-4 OrderListCol" style={{height:"80vh", overflow:"scroll"}}>
              <h5>Customers</h5>
              {/* Pending Order List Item */}
              {this.__showCustomers()}
              {/* /Pending Order List Item */}
          </div>
          <div className="col-lg-4 OrderListCol" style={{height:"80vh", overflow:"scroll"}}>
              <h5>Employees</h5>
              {/* Paid Order List Item */}
              {/* {this.__showPaidOrders()} */}
              {/* /Paid Order List Item */}
          </div>
          <div className="col-lg-4 OrderListCol" style={{height:"80vh", overflow:"scroll"}}>
              <h5>Admin</h5>
              {/* Served Order List Item */}
              {this._sh}
              {/* /Served Order List Item */}
          </div>
      </div>
    </div>
    );
  }

  __showCustomers() {
    if (this.state.userData.length > 0) {
      return this.state.userData.map(user => {
        // if(user.order_status == 0){
          return (
            <UserListItem
              key={user._id}
              user={user.name}
              email={user.email}
            />
          );
        // };
      });
    } else {
      return null;
    }
  }

  __showEmployees() {
    if (this.state.orderData.length > 0) {
      return this.state.orderData.map(order => {
        if(order.order_status == 1){
          return (
            <UserListItem
              key={order._id}
              order_id={order._id}
              orderNumber={order.order_number}
              user={order.user_name}
              meals={order.meal_id}
              getOrderData={this._getOrderData}
            />
          );
        };
      });
    } else {
      return null;
    }
  }

  __showAdmin() {
    if (this.state.userData.length > 0) {
      return this.state.userData.map(user => {
        if(user.role === '2'){
          return (
            <UserListItem
              key={user._id}
              user={user.name}
              email={user.email}
            />
          );
        };
      });
    } else {
      return null;
    }
  }

}

export default UsersTab
