import React from 'react'
import {Doughnut} from 'react-chartjs-2';

class OrdersChart extends React.Component {
    constructor(props) {
        super(props);
        this.state =  {data:{
            labels: ["Beakfasts", "Light Meals", "Drinks", "Desserts"],
            datasets: [{
            backgroundColor: 'rgb(255, 99, 132)',
            borderColor: ['rgb(255, 99, 132)','rgb(155, 99, 132)'],
            data: [9, 10, 5, 2],
            }]
        }, options:{
            legend: {
            display: false
            },
            layout: {
                padding: {
                    left: 50,
                    right: 0,
                    top: 0,
                    bottom: 0
                }
            }
        }};
      }

    render() {
        return (
            <div className="chartContainer">
                <div className="col">
                    <h5 className="chartHeadingSmall">ORDERS</h5>
                    <h5 className="chartHeading">Frequently Ordered</h5>
                </div>
                <div className="col">
                < Doughnut data = {this.state.data}
                            options = {this.state.options}
                            height={250}/>
                </div>
            </div>
    );
    }
}    

export default OrdersChart
