import React from 'react';
import axios from "axios";
import Header from './Header';
import CartItem from './CartItem';
import MenuItem from './MenuItem';

class Menu extends React.Component {
    constructor() {
        super();
        this.state = {menuData: [],menuCart:[],filter:"all"};
        this._orderItems = this._orderItems.bind(this);
        this._addToCartHandler = this._addToCartHandler.bind(this);
        this._removeFromCartHandler = this._removeFromCartHandler.bind(this);
        this._deleteFromCartHandler = this._deleteFromCartHandler.bind(this);
    }

    componentDidMount() {
      this._getMenuData();
    }

    _getMenuData() {
      let url = 'https://dev-african-roots.herokuapp.com/api/meals';
      return axios(url, {
          method: 'GET',
          mode: 'no-cors',
          headers: {
            'Access-Control-Allow-Origin': '*',
            'Content-Type': 'application/json',
          },
          withCredentials: false,
          credentials: 'same-origin',
        }).then(response => {
          this.setState({
            menuData: response.data
          });
          console.log(this.state.menuData);
        })
    }

    _orderItems(){

        //add update function here

        this.setState({
            menuCart: []
          });
    }

    _addToCartHandler(name,price,image){

        let menuCart = this.state.menuCart;

        if (menuCart.length < 1) {
            menuCart.push({name:name,price:price,amount:1,image:image});
        } else {
            menuCart = this._searchInventory(menuCart,name,price,image);
        }

        this.setState({
            menuCart: menuCart
          });
          console.log(menuCart); 
    }

    _removeFromCartHandler(name) {

        let menuCart = this.state.menuCart;

        for (var i=0; i < menuCart.length; i++) {
            if (menuCart[i].name == name) {
                menuCart[i].amount  = menuCart[i].amount - 1;
                if (menuCart[i].amount < 1) {
                    menuCart.splice(i,1);
                }
            }
        }

        this.setState({
            menuCart: menuCart
          });
    }

    _deleteFromCartHandler(name) {

        let menuCart = this.state.menuCart;

        for (var i=0; i < menuCart.length; i++) {
            if (menuCart[i].name == name) {
                menuCart.splice(i,1);
            }
        }


        this.setState({
            menuCart: menuCart
          });
    }

    _searchInventory(menuCart,name,price,image){
        let isSame = false;
        for (var i=0; i < menuCart.length; i++) {
            if (menuCart[i].name == name) {
                menuCart[i].amount  = menuCart[i].amount + 1;
                return menuCart;
            } else {
                isSame = true;
            }
        }
        if (isSame = true){
          menuCart.push({name:name,price:price,amount:1,image:image});
          return menuCart;
        }
    }

    _updateCartTotal(){
        let total = 0;
        for (var i=0; i < menuCart.length; i++) {
            total = total + this.state.menuCart[i]
        }
    }

    // _passFilter(items){

    //     let isSame = false;
    //     for (var i=0; i < menuCart.length; i++) {
    //         if (menuCart[i].name == name) {
    //             menuCart[i].quantity  = menuCart[i].quantity + 10;
    //             return menuCart;
    //         } else {
    //             isSame = true;
    //         }
    //     }
    //     if (isSame = true){
    //       menuCart.push({name:name,quantity:quantity,unit:unit});
    //       return menuCart;
    //     }
    // }

    render() {
      return (
        <div>
                <div className="background" style={{backgroundSize: '200px',backgroundImage: `url(assets/img/Asset%203.svg)`}}></div>
                {/*Nav*/}
                <Header />
                {/*/Nav*/}


                {/*Body Container*/}
                <div className="container">
                    <div className="row">

                    {/*Menu Cards Container*/}
                    <div className="col-12 col-lg-8 cardsContainer" style={{margin: '0px',marginBottom:'-100px',height:'80vh',overflow:'scroll'}}>
                        <div className="row" style={{margin: '0px'}}>

                    {/*Menu Card*/}
                    {this.__showMenuItems()}
                    {/*/Menu Card*/}
                        </div>
                    </div>
                    {/*/Menu Cards Container*/}

                    {/* Cart Container */}
                    <div className="col cartContainer ccDown" id="cartContainer" ref={this.cartContainer}>
                        <div className="row d-lg-none cartHead" id="cartHeadBtn">
                            <div className="col-12 cartTab">
                                <div id="tabDown" ref={this.tabDown}></div><img src="assets/img/Shivron.svg" id="tabUp" ref={this.tabUp}></img></div>
                            <div className="col-4 noPadding">
                                <p className="basketHead"><img src="assets/img/shopping-basket-7.svg" className="cartImg2"></img>Basket</p>
                            </div>
                            <div className="col">
                                <p className="cartAmountTxt" ref={this.cartAmountTxt}>{/*Cart Item Amount*/}4 Items</p>
                            </div>
                            <div className="col-4 noPadding" id="basketTotalColumn" ref={this.basketTotalColumn}>
                                <p className="basketTotal">{/*Cart Total*/}{this.state.menuCart}</p>
                            </div>
                        </div>
                        <div className="row cartContent">
                            <div className="col-12 d-none d-lg-block cartHeadColumn">
                                <p className="text-right cartAmountTxt" ref={this.cartAmountTxt}>{/*Cart Item Amount*/}4 Items</p>
                            </div>
                            <div className="col-12 cartBody">

                                {/*Cart Item*/}
                                {this.__showCartItems()}
                                {/*/Cart Item*/}

                                <div className="heightStopper"></div>
                            </div>
                            <div className="col-12 CartTotalSection">
                                <div className="row">
                                    <div className="col">
                                        <p className="FoodNameLabelBlack">Total</p>
                                    </div>
                                    <div className="col">
                                        <p className="text-right cartTotalPriceLabel">{/*Cart Total*/}R 100.00</p>
                                    </div>
                                </div>
                                <div className="row" id="loginToOrder">{/*Before Login*/}
                                    <div className="col"><button className="btn btn-dark btn-lg btnLogin" type="button" id="loginToOrderBtn" data-toggle="modal" data-target="loginModal">Login to Order</button></div>
                                </div>
                                <div className="row d-none" id="orderBtnRow">{/*After Login*/}
                                    <div className="col-4 helloTxtCol">
                                        <h6 className="text-left d-lg-flex">Hello, {/*User Name*/}Paul</h6>
                                    </div>
                                    <div className="col-8"><button className="btn btn-dark btn-lg btnLogin" type="button" id="orderBtn">Order</button></div>
                                </div>
                            </div>
                        </div>
                    </div>  
                    {/* /Cart Container */}

                    {/* Receipts Container */}
                    {/* <Reciepts /> */}
                    {/* /Receipts Container */}
                    </div>
                </div>

                {/* /Body Container */}

                {/*Menu Item Details Modal*/}
                {/* <menuItemDetails /> */}
                {/*/Menu Item Details Modal*/}

                {/*Log In Modal*/}
                {/* <Login /> */}
                {/*/Log In Modal*/}

                {/*Sign Up Modal*/}
                {/* <CreateAccount /> */}
                {/*/Sign Up Modal*/}

            </div>

        );
    }

    __showMenuItems() {
        // filteredData = _passFilter(this.state.inventoryData);
        if (this.state.menuData.length > 0) {
          return this.state.menuData.map(item => {
            return (
            <MenuItem
                key={item._id}
                name={item.name}
                price={item.price_id}
                image={item.image}
                addToCart={this._addToCartHandler}
            />
            );
          });
        } else {
          return null;
        }
      }

      __showCartItems() {

        if (this.state.menuCart.length > 0) {
          return this.state.menuCart.map(item => {
            return (
            <CartItem
                key={item.name}
                name={item.name}
                price={item.price}
                amount={item.amount}
                image={item.image}
                addToCart={this._addToCartHandler}
                removeFromCart={this._removeFromCartHandler}
                deleteFromCart={this._deleteFromCartHandler}
            />
            );
          });
        } else {
          return null;
        }
      }
}

export default Menu
