//Tab For Viewing a List of Orders

import React from 'react'
import axios from "axios";
import OrderListItem from './OrderListItem';
import OrderListItemBtn from './OrderListItemBtn';

class OrderListTab extends React.Component {
  constructor() {
      super();
      this.state = {orderData: [],mealData: []};
  }

  componentDidMount() {
    this._getOrderData();
    this._getMealData()
  }

  _getOrderData() {
    let url = 'https://dev-african-roots.herokuapp.com/api/orders';
    return axios(url, {
        method: 'GET',
        mode: 'no-cors',
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
        },
        withCredentials: false,
        credentials: 'same-origin',
      }).then(response => {
        this.setState({
          orderData: response.data
        });
        console.log(this.state.orderData);
      })
  }

  _getMealData() {
    let url = 'https://dev-african-roots.herokuapp.com/api/meals';
    return axios(url, {
        method: 'GET',
        mode: 'no-cors',
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
        },
        withCredentials: false,
        credentials: 'same-origin',
      }).then(response => {
        this.setState({
          mealData: response.data
        });
        console.log(this.state.mealData);
      })
  }

  _search(nameKey, array){
    for (var i=0; i < array.length; i++) {
      console.log(array[i]);
        if (array[i].name === nameKey) {
          console.log(array[i]);
            return array[i];
        }
    }
  }

  render() {
    return (
    <div className="tab-pane" role="tabpanel" id="tab-2">
      <div className="row">
          <div className="col-lg-4 OrderListCol">
              <h5>Pending</h5>
              {/* Pending Order List Item */}
              {this.__showPendingOrders()}
              {/* /Pending Order List Item */}
          </div>
          <div className="col-lg-4 OrderListCol">
              <h5>Paid</h5>
              {/* Paid Order List Item */}
              <OrderListItemBtn />
              {/* /Paid Order List Item */}
          </div>
          <div className="col-lg-4 OrderListCol">
              <h5>Served</h5>
              {/* Served Order List Item */}
              <OrderListItem />
              {/* /Served Order List Item */}
          </div>
      </div>
    </div>
    );
  }

  __showPendingOrders() {
    if (this.state.orderData.length > 0) {
      return this.state.orderData.map(order => {
        if(order.order_status == 0){
          return (
            <OrderListItem
              key={order._id}
              orderNumber={order.order_number}
              meals={this._search(order.meal_id,this.state.mealData)}
            />
          );
        };
      });
    } else {
      return null;
    }
  }
}

export default OrderListTab
