import React from 'react'

class CartItem extends React.Component {
    constructor(props) {
        super(props);
        this._handleClickPlus = this._handleClickPlus.bind(this);
        this._handleClickMinus = this._handleClickMinus.bind(this);
      }

    _handleClickPlus(){
     this.props.addToCart(this.props.name,this.props.quantity)
    }

    _handleClickMinus(){
        this.props.removeFromCart(this.props.name,this.props.quantity)
       }

    render() {
        return (
          <div>
          <div className="row cartItem">
            <div className="col-3 cartItemImg">{/*Food Image*/}</div>
            <div className="col cartItem">
              <div className="row">
                <div className="col">
                  <p className="FoodNameLabelBlack">{/*Food Name*/}{this.props.name}</p>
                </div>
              </div>
              <div className="row">
                <div className="col cartItemPriceCol">
                  <p className="FoodPriceLabelMoneyLeft">{/*Food Price*/}R {this.props.price}.00</p>
                </div>
              </div>
            </div>
            <div className="col cartItem">
              <div className="row">
                <div className="col"><i className="la la-close float-right hoverItem"></i></div>
              </div>
              <div className="row cartItemAmountSelect">
                <div className="col d-inline-flex cartItemAmountSelectCol"><i className="la la-minus float-left hoverItem" onClick={this._handleClickMinus}></i>
                  <p className="text-center FoodNameLabelBlack">{/*Food Amount*/}1</p><i className="la la-plus float-right hoverItem" onClick={this._handleClickPlus}></i></div>
              </div>
            </div>
          </div>
          <div className="cartItemDevider"></div>
        </div>
    );
    }
}    

export default CartItem
