// Receipt Item Component

import React from 'react'
import RecieptMenuItem from './RecieptMenuItem';
import axios from "axios";
import RecieptQRRow from './RecieptQRRow';
import { throws } from 'assert';

class Reciept extends React.Component {
    constructor() {
      super();
      this.state = {orderData: [],mealData: []};
    }

    componentDidMount() {
        this._getMealData();
      }

    _getMealData() {
        let url = 'https://dev-african-roots.herokuapp.com/api/meals';
        return axios(url, {
            method: 'GET',
            mode: 'no-cors',
            headers: {
              'Access-Control-Allow-Origin': '*',
              'Content-Type': 'application/json',
            },
            withCredentials: false,
            credentials: 'same-origin',
          }).then(response => {
            this.setState({
              mealData: response.data
            });
          })
      }

    _searchMeals(mealID, mealData){
        let meals= [];
        for (var i=0; i < mealData.length; i++) {
            if (Array.isArray(mealID)) {
                for (var j=0; j < mealID.length; j++) {
                    if (mealData[i]._id === mealID[j]) {
                        meals.push(mealData[i])
                    }
                }
            }
            else if (mealData[i]._id === mealID) {
                meals.push(mealData[i])
            } 
        }

        return meals;
      }


    render() {
      return (
        <div className="col-12">
        <div className="row receiptBody">
            <div className="col-12">
                <h6>{/*Order Timestamp(elapsed Time)*/}two hours ago</h6>
                <div><a className="btn btn-light d-lg-flex align-items-lg-center collapseHead" data-toggle="collapse" aria-expanded="false" aria-controls="collapse-1" role="button" href="#collapse-1"><img src="assets/img/fa99fa2adb081f981d01e52b3f248bab.jpg" className="MenuItemThumbnail"></img><i className="la la-angle-down d-lg-flex ml-auto"></i></a>
                    <div className="collapse receiptsCard" id="collapse-1">
                        <div className="row">
                            <div className="col-12 d-inline-flex">
                                <p className="receiptTimestamp">{/*Order Timestamp(Date)*/}02 March 2019</p>
                                <p className="d-xl-flex ml-auto receiptTimestamp">{/*Order Timestamp(Day)*/}10:05</p>
                            </div>
                        </div>
                        <div className="row">
                            {/*Menu Item*/}
                            {this.__showMenuItem()}
                            {/*/Menu Item*/}

                            <div className="col-12 d-inline-flex totalReceiptCol">
                                <p className="FoodNameLabelReceipt">Total</p>
                                <p className="d-xl-flex ml-auto FoodPriceLabelReceipt">{/*Order Total*/}R {this._}.00</p>
                            </div>
                        </div>

                        {/*QR Row*/}
                        <RecieptQRRow />
                        {/*/QR Row*/}
                    </div>
                </div>
            </div>
        </div>
    </div>
        );
    }

    __showMenuItem() {
        let meals = this._searchMeals(this.props.meals,this.state.mealData);
        if (meals.length > 0) {
        return meals.map(meal => {
            return (
                <RecieptMenuItem
                key={meal._id}
                name={meal.name}
                price={meal.price_id}
                />
            );
        });
        } else {
        return null;
        }
    }
}

export default Reciept
