// Menu Item Component

import React from 'react'
import Fade from 'react-reveal/Fade'

class MenuItem extends React.Component {
    constructor(props) {
        super(props);
        this._handleClick = this._handleClick.bind(this);
      }

    _handleClick(){
     this.props.addToCart(this.props.name,this.props.price,this.props.image);
    }

    render() {
        const availible = this.props.isAvailible;
        let view;

        if (availible == null) {
            view = 
                <li className="nav-item navHamburgerItem" role="presentation"><a className="nav-link hamburgerNavLink" href="#" data-toggle="modal" data-target="#loginModal">Log in</a></li>
            view2 =     
                <li className="nav-item navHamburgerItem" role="presentation"><a className="nav-link hamburgerNavLink" href="#" data-toggle="modal" data-target="#signUpModal">Sign Up</a></li>
        } else {
            view = 
            <li class="nav-item dropdown">
                  <a class="nav-link dropdown-toggle" href="#" id="navbarDropdownMenuLink" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
                  <i class="material-icons greenIcon" style={{marginTop: '15px',paddingRight: '5px'}}>account_circle</i>{sessionStorage.getItem('userName')}
                  </a>
                  <div class="dropdown-menu" aria-labelledby="navbarDropdownMenuLink" style={{textAlign:'center',width: '100px', border:"none", boxShadow: '0px 1px 15px rgba(0, 0, 0, 0.2)',borderRadius:'5px'}}>
                  <li className="nav-item" role="presentation"><a className="nav-link hamburgerNavLink" onClick={this._handleLogout}>log out</a></li>
                  </div>
                </li>
        }

        return (
            <Fade bottom>
            <div className="col-6 col-sm-4 col-md-3 menuItem">
            <div className="card" id="menuItemCard">
                <div className="card-body" style={{backgroundImage: 'url(meals-pictures/' + this.props.image+')',backgroundSize: "cover",backgroundPosition: "center", padding: "0"}}>{/*Food Image*/}
                    <div className="cartTopHalf">
                        <div className="row blurredRow">
                            <div className="col foodNameBlock">
                                <div className="blurredImg" style={{backgroundImage: 'url(meals-pictures/' + this.props.image+')'}}></div>
                                <h4 className="FoodNameLabelWhite">{/*Food Name*/}{this.props.name}</h4>
                            </div>
                        </div>
                    </div>
                    <div className="row detailsRow">
                        <div className="col" style={{padding: "0"}}>
                            <h4 className="FoodPriceLabelMoney">{/*Food Price*/}R {this.props.price}.00</h4>
                            <div className="softLine textSoft"></div>
                            <p className="addBasketBtn DarkGreen" onClick={this._handleClick}><img src="assets/img/shopping-basket-4.svg" className="cartImg" alt="Basket"></img>Add to Basket</p>
                        </div>
                    </div>
                </div>
            </div>
        </div> 
        </Fade>
    );
    }
}    

export default MenuItem
