//Tab For Viewing a List of Orders

import React from 'react'
import axios from "axios";
import OrderListItem from './OrderListItem';

class OrderListTab extends React.Component {
  constructor() {
      super();
      this.state = {orderData: []};
  }

  componentDidMount() {
    this._getOrderData();
  }

  _getOrderData() {
    let url = 'https://dev-african-roots.herokuapp.com/api/orders';
    return axios(url, {
        method: 'GET',
        mode: 'no-cors',
        headers: {
          'Access-Control-Allow-Origin': '*',
          'Content-Type': 'application/json',
        },
        withCredentials: false,
        credentials: 'same-origin',
      }).then(response => {
        this.setState({
          orderData: response.data
        });
        console.log(this.state.orderData);
      })
  }

  render() {
    return (
    <div className="tab-pane" role="tabpanel" id="tab-2">
      <div className="row">
          <div className="col-lg-4 OrderListCol">
              <h5>Pending</h5>
              {/* Pending Order List Item */}
              <OrderListItem />
              {/* /Pending Order List Item */}
          </div>
          <div className="col-lg-4 OrderListCol">
              <h5>Paid</h5>
              {/* Paid Order List Item */}
              <OrderListItem />
              {/* /Paid Order List Item */}
          </div>
          <div className="col-lg-4 OrderListCol">
              <h5>Served</h5>
              {/* Served Order List Item */}
              <OrderListItem />
              {/* /Served Order List Item */}
          </div>
      </div>
    </div>
    );
  }

  __showPendingOrders() {
    if (this.state.comments.length > 0) {
      return this.state.comments.map(comment => {
        return (
          <Comment
            key={comment.key}
            name={comment.name}
            comment={comment.comment}
            avatar={comment.avatar}
          />
        );
      });
    } else {
      return null;
    }
  }
}

export default OrderListTab
